const tiltCard = document.querySelector(".card-style");
tiltCard && VanillaTilt.init(tiltCard, {
  max: 20,
  speed: 1000,
  reverse: !0,
  perspective: 2e3,
  easing: "cubic-bezier(0.0, 0, 0.2, 1)"
});

const card = document.querySelector(".card"),
      frontButton = document.querySelector(".front-button"),
      backButton = document.querySelector(".back-button");

card && frontButton && frontButton.addEventListener("click", () => {
  card.classList.toggle("front");
});
card && backButton && backButton.addEventListener("click", () => {
  card.classList.toggle("front");
});

function copyText(t) {
  navigator.clipboard.writeText(t).then(() => {
    const t = document.getElementById("alert");
    t && (t.style.display = "block", setTimeout(() => {
      t.style.display = "none";
    }, 2e3));
  }).catch(t => {
    console.error("Failed to copy text:", t);
  });
}

const video = document.getElementById("video-background");
video && video.addEventListener("contextmenu", t => {
  t.preventDefault();
});

document.addEventListener("DOMContentLoaded", () => {
  const t = document.querySelector(".services-bio");
  t && new TypeIt(t, {
    speed: 200,
    loop: !0
  })
    .type("I am ", { delay: 100 })
    .type("a developer,", { delay: 15000 }) // 12 letters
    .delete(17)
    .type("a web developer", { delay: 15000 }) // 16 letters
    .delete(15)
    .type("and a gamer!", { delay: 15000 }) // 12 letters
    .delete(12)
    .go();
});
